"use strict"

exports.Dictionary = function (){
	var items = {};

	this.has = function(key){
		return key in items;
	};

	this.set = function(key, value){
		items[key] = value;
	};

	this.remove = function(key){
		if(this.has(key)){
			delete items[key];
			return true;
		}else{
			return false;
		}
	};

	this.get = function(key){
		return this.has(key) ? items[key] : undefined;
	};

	this.keys = function(){
		var keys = [];
		for(var key in items){
			if(this.has(key)){
				keys.push(key);
			}
		}

		return keys;
	};

	this.values = function(){
		var values = {};
		for(var k in items){
			if(this.has(k)){
				values.push(items[k]);
			}
		}
		return values;
	};

	this.clear = function(){
		items = {};
	};

	this.size = function(){
		return Object.keys(items).length;
	};

    this.sizeLegacy = function() {
        var count = 0;
        for (var prop in items) {
            if (items.hasOwnProperty(prop)) {
                ++count;
            }
        }
        return count;
    };

	this.getItems = function(){
		return items;
	};

}
