"use strict"

function ProrityQueue(){
	var queue = [];

	function QueueElement(element, priority){
		this.element = element;
		this.priority = priority;
	}

	this.enqueue = function(element, priority){

		if(this.isEmpty()){
			queue.push(element);
		}else{
			var added = false;
			for(var i = 0; i < queue.length; i++){
				console.log(queue[i]);
				if(element.priority < queue[i].priority){
					queue.splice(i, 0, element);
					added = true;
					break;
				}
			}

			if(!added){
				queue.push(element);
			}
		}
	};

	this.dequeue = function(){
		return queue.shift();
	};

	this.front = function(){
		return queue[0];
	};

	this.isEmpty = function(){
		return queue.length == 0;
	};

	this.size = function(){
		return queue.length;
	};

	this.print = function(){
		console.log(queue);
	};
}

module.exports = ProrityQueue;