"use strict"

exports.Queue = function(){
	var queue = [];

	this.enqueue = function(element){
		queue.push(element);
	};

	this.dequeue = function(){
		return queue.shift();
	};

	this.front = function(){
		return queue[0];
	};

	this.isEmpty = function(){
		return queue.length == 0;
	};

	this.size = function(){
		return queue.length;
	};

	this.print = function(){
		console.log(queue);
	};
};

exports.PriorityQueue = function () {
    var queue = [];

    function QueueElement(element, priority) {
        this.element = element;
        this.priority = priority;
    }

    this.enqueue = function(element, priority) {
        var queueElement = new QueueElement(element, priority);

        if (this.isEmpty()) {
            console.log('empty');
            queue.push(queueElement);
        } else {
            var added = false;
            console.log(0 < queue.length);
            for (var i = 0; i <= queue.lenght; i++) {
                console.log(queueElement.priority);
                console.log(queue[i].priority);
                if (queueElement.priority < queue[i].priority) {
                    console.log(i);
                    queue.splice(i, 0, queueElement);
                    added = true;
                    break;
                }
            }
            if (!added) {
                console.log('added');
                queue.push(queueElement);
            }
        }
    }

    this.dequeue = function() {
        return queue.shift();
    };

    this.front = function() {
        return queue[0];
    };

    this.isEmpty = function() {
        return queue.length == 0;
    };

    this.size = function() {
        return queue.length;
    };

    this.print = function() {
        console.log(queue);
    };
};


exports.hotPotato = function(nameList, num){
	var queue = new Queue();

	for(var i = 0; i < nameList.length; i++){
		queue.enqueue(nameList[i]);
	}

	var eliminated = '';
	while(queue.size() > 1){
		for(var i = 0;i < num; i++){
			queue.enqueue(queue.dequeue());
		}
		eliminated = queue.dequeue();
		console.log(eliminated + '-->在击鼓传花游戏中被淘汰');
	}
	return queue.dequeue();
}

